#ifndef __FOO_MAPPER_H_ #define __FOO_MAPPER_H_
#ifdef HAVE_CONFIG_H #include <config.h> #endif
#include "../scconf/scconf.h" #include "mapper.h"
/* include here container declaration if you intend to declare and use several instances of this mapper */
typedef struct myContext_st { /* local declarations here */ } myContext;
#ifdef FOO_MAPPER_STATIC
#ifndef __FOO_MAPPER_C_ #define FOO_EXTERN extern #else #define FOO_EXTERN #endif FOO_EXTERN mapper_module * foo_mapper_module_init(scconf_block *blk,const char *mapper_name); #undef FOO_EXTERN
/* end of static (if any) declarations */ #endif
/* End of foo_mapper.h */ #endif

Note that this skeleton file provides support for either static or dynamic linking. Our suggestion is follow this convention, making your mapper as versatile as possible


/*
 * PAM-PKCS11 FOO mapper module
 * -------------- YOUR COPYRIGHTS CREDITS HERE --------------------
 * Copyright (C) 2005 Juan Antonio Martinez <jonsito@teleline.es>
 * pam-pkcs11 is copyright (C) 2003-2004 of Mario Strasser <mast@gmx.net>
 *
 * This library is free software; you can redistribute it and/or
 * modify it under the terms of the GNU Lesser General Public
 * License as published by the Free Software Foundation; either
 * version 2.1 of the License, or (at your option) any later version.
 *
 * This library is distributed in the hope that it will be useful,
 * but WITHOUT ANY WARRANTY; without even the implied warranty of
 * MERCHANTABILITY or FITNESS FOR A PARTICULAR PURPOSE.  See the GNU
 * Lesser General Public License for more details.
 *
 * You should have received a copy of the GNU Lesser General Public
 * License along with this library; if not, write to the Free Software
 * Foundation, Inc., 59 Temple Place, Suite 330, Boston, MA 02111-1307 USA
 *
 * $Id$
